mapper.select:
select

from jy_law_info
where DEL_FLAG = '0'

    AND SERIAL_NO = #{serialNo,jdbcType=VARCHAR}


    AND LAW_NO = #{lawNo,jdbcType=VARCHAR}


    AND STATE = #{state,jdbcType=VARCHAR}


    AND IS_RETURN = #{isReturn,jdbcType=CHAR}


    AND REASON = #{reason,jdbcType=VARCHAR}


    AND LAW_MONEY = #{lawMoney,jdbcType=DOUBLE}


    AND LAW_ORG_ID = #{lawOrgId,jdbcType=INTEGER}


    AND LAW_ORG_NAME = #{lawOrgName,jdbcType=VARCHAR}


    AND ADJUST_ORG_CODE = #{adjustOrgCode,jdbcType=INTEGER}


    AND ADJUST_ORG_NAME = #{adjustOrgName,jdbcType=VARCHAR}


    AND ADJUST_POINT_CODE = #{adjustPointCode,jdbcType=VARCHAR}


    AND ADJUST_POINT_NAME = #{adjustPointName,jdbcType=VARCHAR}


    AND APPLICANT = #{applicant,jdbcType=VARCHAR}


    AND RESPONDENT = #{respondent,jdbcType=VARCHAR}


    AND FACT_REASON = #{factReason,jdbcType=VARCHAR}


    AND PAY_DATE = #{payDate,jdbcType=TIMESTAMP}


    AND ADJUST_DATE = #{adjustDate,jdbcType=TIMESTAMP}


    AND ADJUST_RESULT = #{adjustResult,jdbcType=CHAR}


    AND ADJUST_RESULT_REMARK = #{adjustResultRemark,jdbcType=VARCHAR}


    AND CREATE_BY = #{createBy,jdbcType=INTEGER}


    AND ADJUST_NAME = #{adjustName,jdbcType=VARCHAR}


    AND CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}


    AND FILING_DATE = #{filingDate,jdbcType=TIMESTAMP}


    AND FILING_NAME = #{filingName,jdbcType=VARCHAR}


    AND IS_DEFERRED_CHARGES = #{isDeferredCharges,jdbcType=CHAR}


    AND ACCEPTANCE_FEE = #{acceptanceFee,jdbcType=DOUBLE}


    AND JURISDICTION_REASON = #{jurisdictionReason,jdbcType=CHAR}


    AND APPLICATION_PROCEDURE = #{applicationProcedure,jdbcType=CHAR}


    AND COURT_DATE = #{courtDate,jdbcType=TIMESTAMP}


    AND IS_SMALL_AMOUNT = #{isSmallAmount,jdbcType=CHAR}


    AND COURT_ID = #{courtId,jdbcType=INTEGER}


    AND COURT_NUM = #{courtNum,jdbcType=VARCHAR}


    AND CHIEF_JUDGE_ID = #{chiefJudgeId,jdbcType=INTEGER}


    AND CLERK_ID = #{clerkId,jdbcType=INTEGER}


    AND MEMBER_ONE_ID = #{memberOneId,jdbcType=INTEGER}


    AND MEMBER_TWO_ID = #{memberTwoId,jdbcType=INTEGER}


    AND UNDERTAKER_ID = #{undertakerId,jdbcType=INTEGER}


    AND UNDERTAKER_PHONE = #{undertakerPhone,jdbcType=VARCHAR}


