    AND CLOSE_CASE_TYPE = #{closeCaseType,jdbcType=CHAR}


    AND CLOSE_CASE_SUGGEST = #{closeCaseSuggest,jdbcType=VARCHAR}


    AND JUDGE_CERTIFICATION = #{judgeCertification,jdbcType=VARCHAR}


    AND DETERMINE_REASON = #{determineReason,jdbcType=VARCHAR}


    AND DETERMINE_CONTENT = #{determineContent,jdbcType=VARCHAR}


    AND NEXT_COURT_DATE = #{nextCourtDate,jdbcType=TIMESTAMP}


    AND RECEIVE_DATE = #{receiveDate,jdbcType=TIMESTAMP}


    AND REGION_NAME = #{regionName,jdbcType=VARCHAR}


    AND STANDARD_YEAR = #{standardYear,jdbcType=VARCHAR}


    AND HOUSEHOLD = #{household,jdbcType=CHAR}


    AND DEATH_DATE = #{deathDate,jdbcType=TIMESTAMP}


    AND COMPENSATE_RATE = #{compensateRate,jdbcType=DOUBLE}


    AND COMPENSATE_STANDARD = #{compensateStandard,jdbcType=VARCHAR}


    AND APPLY_TOTAL = #{applyTotal,jdbcType=DOUBLE}


    AND PAID_TOTAL = #{paidTotal,jdbcType=DOUBLE}


    AND FEE_DETAIL = #{feeDetail,jdbcType=VARCHAR}


    AND COMPENSATE_TABLE = #{compensateTable,jdbcType=VARCHAR}


    AND APPLY_ARREE_NAME = #{applyArreeName,jdbcType=VARCHAR}


    AND RESPONDENT_AGREE_NAME = #{respondentAgreeName,jdbcType=VARCHAR}


    AND APPLY_JUDGE = #{applyJudge,jdbcType=BIT}


    AND PAY_STATE = #{payState,jdbcType=CHAR}


    AND REPLY_TOTAL = #{replyTotal,jdbcType=DOUBLE}


    AND CLAIM_STATE = #{claimState,jdbcType=VARCHAR}


    AND CODE_FILE_NAME = #{codeFileName,jdbcType=VARCHAR}


    AND CLAIM_MESSAGE = #{claimMessage,jdbcType=VARCHAR}


    AND PAYEE_NAME = #{payeeName,jdbcType=VARCHAR}


    AND PAYEE_ACCOUNT = #{payeeAccount,jdbcType=VARCHAR}


    AND OPENING_BANK = #{openingBank,jdbcType=VARCHAR}


    AND JUDGE_CASE_ID = #{judgeCaseId,jdbcType=VARCHAR}


    AND COURT_CODE = #{courtCode,jdbcType=VARCHAR}


    AND DISABILITY_GRADE_ID = #{disabilityGradeId,jdbcType=VARCHAR}


    AND CHIEF_NAME = #{chiefName,jdbcType=VARCHAR}


    AND CLERK_NAME = #{clerkName,jdbcType=VARCHAR}


