    AND LAW_PERSON_TYPE = #{lawPersonType,jdbcType=VARCHAR}


    AND IS_ADJUST = #{isAdjust,jdbcType=VARCHAR}


    AND ADJUST_PERSON_ID = #{adjustPersonId,jdbcType=INTEGER}


    AND IS_SEND_APPRAISAL = #{isSendAppraisal,jdbcType=CHAR}


    AND IS_APPRAISAL_FINISH = #{isAppraisalFinish,jdbcType=CHAR}


    AND OLD_IS_SEND_APPRAISAL = #{oldIsSendAppraisal,jdbcType=CHAR}


    AND OLD_IS_APPRAISAL_FINISH = #{oldIsAppraisalFinish,jdbcType=CHAR}


    AND CASE_END_DATE = #{caseEndDate,jdbcType=TIMESTAMP}


    AND LITIGATION_FLAG = #{litigationFlag,jdbcType=VARCHAR}


    AND PLAT_FORM = #{platForm,jdbcType=VARCHAR}


    AND AHDM = #{ahdm,jdbcType=VARCHAR}


    AND REGION_CODE = #{regionCode,jdbcType=VARCHAR}


    AND DZAH = #{dzah,jdbcType=VARCHAR}


    AND id_bak = #{idBak,jdbcType=INTEGER}


    AND ajbs = #{ajbs,jdbcType=VARCHAR}


    AND ACCIDENT_TYPE = #{accidentType,jdbcType=VARCHAR}


    AND RESPONSIBILITY = #{responsibility,jdbcType=VARCHAR}


    AND SUFFER_NATURE = #{sufferNature,jdbcType=VARCHAR}


    AND accident_date = #{accidentDate,jdbcType=TIMESTAMP}


    AND INJURE_FEE_DETAIL = #{injureFeeDetail,jdbcType=VARCHAR}


    AND REGULATION_NO = #{regulationNo,jdbcType=VARCHAR}


    AND LITIGATION_REQUEST = #{litigationRequest,jdbcType=VARCHAR}


    AND PLATFORM_ORIGIN = #{platformOrigin,jdbcType=VARCHAR}


    AND TARGET_MONEY = #{targetMoney,jdbcType=DOUBLE}
